import axios from 'axios';
import { useEffect, useState } from 'react';
import './App.css';
import Table from './components/Table';

function App() {
  const [dataTable, setDataTable] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const USER_PER_PAGE = 5;

  useEffect(() => {
    const res = axios('http://localhost:3000/customers')
      .then(res => setDataTable(res.data))
      .catch(err => console.log(err))
      setLoading(false);
      setTotalPages(Math.ceil(res.length / USER_PER_PAGE));
  }, []);

  const column = [
    { heading: 'Serial No', value: 'sno' },
    { heading: 'Customer Name', value: 'customer_name' },
    { heading: 'Age', value: 'age' },
    { heading: 'Phone', value: 'phone' },
    { heading: 'Customer Location', value: 'customer_location' },
    { heading: 'Created At', value: 'created_at' },
  ]
  const handleClick = num => {
    setPage(num);
  }

  return (
    <div className="App">
      <h1>Customer Application</h1>
      <Table data={dataTable} column={column} />
      <Pagination totalPages={totalPages} handleClick={handleClick} />
    </div>
  );
}

export default App;
