import axios from 'axios';
import { useEffect, useState } from 'react';
import './App.css';
import Table from './components/Table';
import Pagination from './components/Pagination';

function App() {
  const [dataTable, setDataTable] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const USER_PER_PAGE = 5;

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:3000/customers', {
          params: {
            page: page,
            limit: USER_PER_PAGE
          }
        });
        setDataTable(response.data.data);
        setTotalPages(Math.ceil(response.data.totalRecords / USER_PER_PAGE)); // Set total pages
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [page]); // Fetch data whenever page changes

  const column = [
    { heading: 'Serial No', value: 'sno' },
    { heading: 'Customer Name', value: 'customer_name' },
    { heading: 'Age', value: 'age' },
    { heading: 'Phone', value: 'phone' },
    { heading: 'Customer Location', value: 'customer_location' },
    { heading: 'Created At', value: 'created_at' },
  ];

  const handleClick = (num) => {
    setPage(num);
  };

  return (
    <div className="App">
      <h1>Customer Application</h1>
      <Table data={dataTable} column={column} />
      <Pagination totalPages={totalPages} handleClick={handleClick} />
      {loading && <p>Loading...</p>}
    </div>
  );
}

export default App;
