// src/CustomerTable.js
import React, { useEffect, useState, useMemo } from 'react';
import axios from 'axios';
import { useTable, usePagination, useSortBy, useGlobalFilter } from 'react-table';
import './CustomerTable.css';

const CustomerTable = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [pageCount, setPageCount] = useState(0);
  const [pageSize] = useState(24); // Number of records per page

  // Fetch data for the current page
  const fetchData = async (pageIndex) => {
    try {
      setLoading(true);
      const result = await axios.get(`http://localhost:3000/customers?page=${pageIndex + 1}&limit=${pageSize}`);
      console.log(`Fetching page ${pageIndex + 1}`, result.data); // Log the API response
      if (result.data && Array.isArray(result.data.data)) {
        setData(result.data.data);
        setPageCount(Math.ceil(result.data.totalRecords / pageSize));
      } else {
        throw new Error('Data format is incorrect, expected an array in data field');
      }
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const columns = useMemo(
    () => [
      { Header: 'Serial No', accessor: 'sno' },
      { Header: 'Customer Name', accessor: 'customer_name' },
      { Header: 'Age', accessor: 'age' },
      { Header: 'Phone', accessor: 'phone' },
      { Header: 'Customer Location', accessor: 'customer_location' },
      { Header: 'Created At', accessor: 'created_at' }
    ],
    []
  );

  // Use react-table hooks
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    prepareRow,
    page,
    canNextPage,
    canPreviousPage,
    pageOptions,
    state: { pageIndex },
    gotoPage,
    nextPage,
    previousPage,
    setGlobalFilter
  } = useTable(
    {
      columns,
      data,
      manualPagination: true, // Tell the usePagination hook that we'll handle our own pagination
      pageCount
    },
    useGlobalFilter,
    useSortBy,
    usePagination
  );

  // Fetch data when the page index changes
  useEffect(() => {
    console.log(`Page index changed to ${pageIndex}`);
    fetchData(pageIndex); // Fetch data whenever pageIndex changes
  }, [pageIndex]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <>
      <input
        value={state.globalFilter || ''}
        onChange={(e) => setGlobalFilter(e.target.value || undefined)}
        placeholder="Search..."
      />
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                  {column.render('Header')}
                  <span>
                    {column.isSorted
                      ? column.isSortedDesc
                        ? ' ðŸ”½'
                        : ' ðŸ”¼'
                      : ''}
                  </span>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {page.map(row => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map(cell => (
                  <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
      <div>
        <button
          onClick={() => {
            console.log('Goto Page 0'); // Debug statement
            gotoPage(0);
          }}
          disabled={!canPreviousPage}
        >
          {'<<'}
        </button>
        <button
          onClick={() => {
            console.log('Previous Page'); // Debug statement
            previousPage();
          }}
          disabled={!canPreviousPage}
        >
          {'<'}
        </button>
        <span>
          Page{' '}
          <strong>
            {pageIndex + 1} of {pageOptions.length}
          </strong>
        </span>
        <button
          onClick={() => {
            console.log('Next Page'); // Debug statement
            nextPage();
          }}
          disabled={!canNextPage}
        >
          {'>'}
        </button>
        <button
          onClick={() => {
            console.log('Goto Last Page'); // Debug statement
            gotoPage(pageOptions.length - 1);
          }}
          disabled={!canNextPage}
        >
          {'>>'}
        </button>
      </div>
    </>
  );
};

export default CustomerTable;
