import React, { useMemo, useState, useEffect } from 'react';
import { useTable, useFilters, usePagination } from 'react-table';
import axios from 'axios';
import { COLUMNS } from './columns';
import './table.css';
import { GlobalFilter } from './GlobalFilter';

export const PaginationTable = () => {
  const columns = useMemo(() => COLUMNS, []);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [pageCount, setPageCount] = useState(0);

  // Function to fetch data from the API
  const fetchData = async (pageIndex, pageSize) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:3000/customers?page=${pageIndex + 1}&limit=${pageSize}`);
      setData(response.data.data); // Adjust based on actual response structure
      setPageCount(Math.ceil(response.data.totalRecords / pageSize)); // Adjust based on actual response structure
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Initial data fetch
  useEffect(() => {
    fetchData(0, 20); // Default pageIndex and pageSize
  }, []);

  // Table instance with pagination and filters
  const tableInstance = useTable({
    columns,
    data,
    initialState: { pageIndex: 0, pageSize: 20 },
    manualPagination: true,
    pageCount,
  }, useFilters, usePagination);

  const { 
    getTableProps, 
    getTableBodyProps, 
    headerGroups, 
    page, 
    nextPage,
    previousPage,
    canNextPage,
    canPreviousPage,
    pageOptions,
    state,
    prepareRow,
  } = tableInstance;

  const { pageIndex, pageSize } = state;

  // Fetch data whenever pageIndex or pageSize changes
  useEffect(() => {
    fetchData(pageIndex, pageSize);
  }, [pageIndex, pageSize]);

  return (
    <>
      <GlobalFilter setGlobalFilter={setGlobalFilter} />
      <table {...getTableProps()}>
        <thead>
          {
            headerGroups.map(headerGroup => (
              <tr {...headerGroup.getHeaderGroupProps()}>
                {
                  headerGroup.headers.map(column => (
                    <th {...column.getHeaderProps()}>
                      {column.render('Header')}
                      <div>{column.canFilter ? column.render('Filter') : null}</div>
                    </th>
                  ))
                }
              </tr>
            ))
          }
        </thead>
        <tbody {...getTableBodyProps()}>
          {
            page.map(row => {
              prepareRow(row);
              return (
                <tr {...row.getRowProps()}>
                  {
                    row.cells.map(cell => (
                      <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                    ))
                  }
                </tr>
              );
            })
          }
        </tbody>
      </table>
      <div>
        <span>
          Page{' '}
          <strong>
            {state.pageIndex + 1} of {pageOptions.length}
          </strong>{' '}
        </span>
        <button onClick={() => previousPage()} disabled={!canPreviousPage}>Previous</button>
        <button onClick={() => nextPage()} disabled={!canNextPage}>Next</button>
      </div>
      {loading && <div>Loading...</div>}
    </>
  )
}
