import React, { useMemo, useState, useEffect } from 'react';
import { useTable, useFilters, usePagination, useGlobalFilter } from 'react-table';
import axios from 'axios';
import { COLUMNS } from './columns';
import './table.css';
import { GlobalFilter } from './GlobalFilter';

export const PaginationTable = () => {
    const columns = useMemo(() => COLUMNS, []);
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [pageCount, setPageCount] = useState(0);

    // Function to fetch data from the API
    const fetchData = async (pageIndex, pageSize, globalFilter) => {
        setLoading(true);
        try {
            const response = await axios.get(`http://localhost:3000/customers?page=${pageIndex + 1}&limit=${pageSize}&filter=${globalFilter || ''}`);
            console.log('API Response:', response.data); // Add this line
            setData(response.data.data || []); // Ensure data is an array
            setPageCount(Math.ceil(response.data.totalRecords / pageSize)); // Adjust based on actual response structure
        } catch (error) {
            console.error('Error fetching data:', error);
        } finally {
            setLoading(false);
        }
    };

    // Table instance with pagination, filters, and global filter
    const tableInstance = useTable({
        columns,
        data,
        initialState: { pageIndex: 0, pageSize: 20 },
        manualPagination: true,
        pageCount,
    }, useGlobalFilter, useFilters, usePagination);

    const { 
        getTableProps, 
        getTableBodyProps, 
        headerGroups, 
        page, 
        nextPage,
        previousPage,
        canNextPage,
        canPreviousPage,
        pageOptions,
        state,
        prepareRow,
        setGlobalFilter,
        state: { globalFilter }
    } = tableInstance;

    const { pageIndex, pageSize } = state;

    // Fetch data whenever pageIndex, pageSize, or globalFilter changes
    useEffect(() => {
        fetchData(pageIndex, pageSize, globalFilter);
    }, [pageIndex, pageSize, globalFilter]);

    // Debugging: Log data and columns to ensure they are defined
    useEffect(() => {
        console.log('Columns:', columns);
        console.log('Data:', data);
    }, [columns, data]);

    return (
        <>
            <GlobalFilter filter={globalFilter} setFilter={setGlobalFilter} />
            <table {...getTableProps()}>
                <thead>
                    {
                        headerGroups.map(headerGroup => (
                            <tr {...headerGroup.getHeaderGroupProps()}>
                                {
                                    headerGroup.headers.map(column => (
                                        <th {...column.getHeaderProps()}>
                                            {column.render('Header')}
                                            <div>{column.canFilter ? column.render('Filter') : null}</div>
                                        </th>
                                    ))
                                }
                            </tr>
                        ))
                    }
                </thead>
                <tbody {...getTableBodyProps()}>
                    {
                        page.map(row => {
                            prepareRow(row);
                            return (
                                <tr {...row.getRowProps()}>
                                    {
                                        row.cells.map(cell => (
                                            <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                        ))
                                    }
                                </tr>
                            );
                        })
                    }
                </tbody>
            </table>
            <div>
                <span>
                    Page{' '}
                    <strong>
                        {state.pageIndex + 1} of {pageOptions.length}
                    </strong>{' '}
                </span>
                <button onClick={() => previousPage()} disabled={!canPreviousPage}>Previous</button>
                <button onClick={() => nextPage()} disabled={!canNextPage}>Next</button>
            </div>
            {loading && <div>Loading...</div>}
        </>
    );
}
